import React ,{useState, useEffect, Fragment} from 'react';
import '../App.css';
import Pagination from '../components/Pagination';
import NavCurrency from '../components/NavCurrency';
import {Helmet} from 'react-helmet';
// import BaseCurrency from '../components/BaseCurrency';
// import CurrencyConverter from '../hoc/withCurrencyConvert';
import HomeCurrencyButton from '../components/HomeCurrencyButton';


const HomePage =() =>{

  const[cryptoList, setCryptoList]= useState([]);
  const[loading, setLoading]= useState(false);
  const[currentPage, setCurrentPage]= useState(1);
  const[currencyPerPage]= useState(5);

  //for base Currency
  const [currencyName, setCurrencyName] = useState('USD'); 
  const [currencySymbol, setCurrencySymbol]= useState('$');
  // const [currentPrice, setCurrentPrice] = useState('');
  // const [currencyPrice, setCurrencyPrice] = useState('');
  const [exchangeRate, setExchangeRate] = useState(1);

  
  


  useEffect(() => {
    setLoading(true);
    fetch('https://api.coinranking.com/v1/public/coins/')
        .then(res=> res.json()).then((data) =>{
      setCryptoList(data.data['coins']);
      console.log('coins' , data.data['coins'])});

      // console.log(cryptoList.map(i=> i.name));
      setLoading(false);  
     }

      , []);
        //Get currency
        const indexOfLastCurrency= currentPage * currencyPerPage;
        const indexOfFirstCurrency = indexOfLastCurrency - currencyPerPage;
        const currencyList= cryptoList.slice(indexOfFirstCurrency, indexOfLastCurrency);
        
        //change page
        const paginate =(number) => setCurrentPage(number);
        

        
        console.log('cryptoList', cryptoList)

        console.log('CurrencyList', currentPage)

        // console.log('CurrencyPrice', currencyPrice)

        


  return (
    <Fragment> 
    <Helmet><title> Cryptowatch</title></Helmet>
    
    <HomeCurrencyButton       
                  setCurrencyName={setCurrencyName} 
                  setCurrencySymbol={setCurrencySymbol}
                  setExchangeRate={setExchangeRate}
                  currencyName = {currencyName}
                  currencySymbol={currencySymbol}
                  exchangeRate={exchangeRate}
                  name = 'Dashboard'
                  />

        <div id='home'>
            <div className='cryptoList'>
            {/* <ExchangeRate from ={'USD'} to={'EUR'} value= {280}/> */}
            <NavCurrency currencyList={currencyList} 
            currencySymbol={currencySymbol}
            currencyName = {currencyName}
            exchangeRate={exchangeRate}
            loading={loading}/>
            </div>  
            <Pagination currencyPerPage={currencyPerPage} 
            totalCurrencies={cryptoList.length} 
            paginate={paginate}/> 
            
            </div>
    </Fragment>
    );
}
    
export default HomePage;
